class Tools {
    static let shareInstance = Tools()
    private init() {}
    
    func showMsg(msg: String) {
        DispatchQueue.main.async {
            SVProgressHUD.show(withStatus: msg, maskType: .black)
        }
    }
    
    func showError(errorInfo: String) {
        DispatchQueue.main.async {
            SVProgressHUD.showError(withStatus: errorInfo, maskType: .black)
        }
    }
    
    func showError(errorCode: LONG) {
        DispatchQueue.main.async {
            let errorMsg: String = String(format: "error:%08x", arguments: [errorCode])
            SVProgressHUD.showError(withStatus: errorMsg, maskType: .black)
        }
    }
    
    func showSuccess(msg: String) {
        DispatchQueue.main.async {
            SVProgressHUD.showSuccess(withStatus: msg, maskType: SVProgressHUDMaskType.black)
        }
    }
    
    func hideMsg() {
        DispatchQueue.main.async {
            SVProgressHUD.dismiss()
        }
    }
}
